import React, { useState, useEffect } from 'react';
import './LibraryCSS/Saved.css';


function SavedGames() {

  const [gameData, setGameData] = useState([]);
  const [link, setLink] = useState([]);

  //From localStorage, get names, return them, and search for games in 
  //custom Google Search API, returning these as images (need to dynamically
  //set width/height).

  useEffect(() => {
    const libraryGames = [];
    for (var x = 0; x<localStorage.length; x++){
      const gameName = localStorage.getItem(localStorage.key(x)).replace(/"/g, '');
      libraryGames.push(gameName);
    }

    setGameData(libraryGames);
  }, []);

  useEffect(() => {
    const linkList = [];
    for (var y = 0; y<gameData.lengthl; y++){
      const linkData = gameData[y].replace(' ', '+');
      linkList.push('https://store.steampowered.com/search/?term=' + linkData);
      console.log(linkList[y])
    }
    setLink(linkList);
  }, [])


  return (
    <div className='OuterLibrary'>
      {gameData.length > 0 ? (
        gameData.map((game, index) => (
          <div key={index} className='InnerLibrary' href='https://store.steampowered.com/search/?term='>
            {game}
          </div>
        ))
      ) : (
        <p>NOT found.</p>
      )}
    </div>
  );
}

export default SavedGames;
