import React, {useState, useEffect} from 'react';
import searchImg from './Images/searchImage.png';
import './Search.css';


function Search() {
    const [input, setInput] = useState('')
    const [output, setOutput] = useState('')

    const handleSubmit = async (event) => {
        event.preventDefault();

        const response = await fetch('http://127.0.0.1:8080/api/process-input/', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({userInput: input}),
        })

        .then(response => response.json())
        .then(data => {
            console.log(data)
            setGameData(data.games)
        })

        const data = await response.json();
        setOutput(data.output);
    }

    return (
        <div className = "search">
            <div className = "row">
                <div className = "Search-Middle">
                    <button id="searchButton" onClick={handleSubmit}>

                    </button>
                </div>
                <div className = "Search-Middle">
                    <form className='searchBar' onSubmit={handleSubmit}>
                        <input id='searchpart' className='searchInput' value={input} onChange={(x) => setInput(x.target.value)} type='text' placeholder='Search here...'/>
                    </form>
                    /**Sending the output to Game.js to display from there.*/
                    {output && <Game gameData={output} />}
                </div>
                <br />
                <div className = "Search-Middle">
                    <p></p>
                </div>
                <div className = "Search-Middle">
                    <p></p>
                </div>
            </div>
        </div>
  );
}

export default Search;
