import React, { useState, useEffect } from 'react';
import './LibraryCSS/Saved.css';
import PCLogo from './Images/PCLogo.png'
import axios from 'axios';


function SavedGames() {

  const [gameData, setGameData] = useState([]);
  const [jsonData, setJsonData] = useState([]);

  //From localStorage, get names, return them, and search for games in 
  //custom Google Search API, returning these as images (need to dynamically
  //set width/height).

  //Using a useEffect to continuously load data from local storage and outputing that as our library data.
  useEffect(() => {
    const libraryGames = [];
    var image;
    for (let x = 0; x<localStorage.length; x++){
      //Here, we set the game name, the data for our link/API call, and the key each game is associated with.
      const gameName = localStorage.getItem(localStorage.key(x)).replace(/"/g, '');
      const plusGameName = gameName.replace(' ', '+');
      const linkData = `https://store.steampowered.com/search/?term=${plusGameName}`;
      
      //Fetch the image from the API: if it finds an image, it's stored, otherwise, default image.
      fetchImage();
      
      libraryGames.push({name: gameName, image: jsonData, url: linkData, key: localStorage.key(x)});
    }

    setGameData(libraryGames);
  }, []);

  const handleRemove = (index) => {
    localStorage.removeItem(index);
    console.log("Removed game was: ", index);
    setGameData((prevGame) => prevGame.filter((game) => game.key !== index));
  }

  const fetchImage = async () => {
    try{
      //We make our API call to google, giving it our game name (formatted for URLs) and returning an image.
      const response = await axios.get(`https://www.googleapis.com/customsearch/v1?key=AIzaSyA8yVgNiwh3Mf7OCHvcrQPL4ocikLadk1Y&num=1&searchType=image&start=1&cx=74bd649225d0f43ba&exactTerms=${plusGameName}&fileType=.PNG&imgSize=medium&alt=json`)
      const data = response.data;
      const imageURL = data.link;
      /*
      fetch(`https://www.googleapis.com/customsearch/v1?key=AIzaSyA8yVgNiwh3Mf7OCHvcrQPL4ocikLadk1Y&num=1&searchType=image&start=1&cx=74bd649225d0f43ba&exactTerms=${plusGameName}&fileType=.PNG&imgSize=medium&alt=json`)
        .then(response => response.json())
        .then(json => setJsonData(response.link))
        .catch(error => console.error(error))
      */
      setJsonData(imageURL);
    }
    catch(error){
      console.error("Couldn't retrieve the image...");
      setJsonData(PCLogo)
    }
  }


  return (
    <div>
      {gameData.length > 0 ? (
        gameData.map((game, index) => (
          <div className='OuterLibrary'>
            <div class="gcse-search">
              
            </div>
            <div key={index} className='InnerLibrary'>
              <a href={game.url} target="_blank" rel="noopener noreferrer" className='gameURLs'>
                {game.name}
              </a>
              <button id="removeGame" onClick = {() => handleRemove(game.key)} />
            </div>
          </div> 
        ))
      ) : (
        <div className='OuterLibrary'>
          <p>No games are added to your library!</p>
        </div>
      )}
    </div>
  );
}

export default SavedGames;
