import React, {useState} from 'react';
import searchImg from './Images/searchImage.png';
import './Search.css';


function Search() {
  const [search, setInput] = useState('');

  const handleSearch = ({target}) => {
    const newSearch = target.value;
    setInput(newSearch);
  };

  return (
    <div className = "search">
        <div className = "row">
            <div className = "Search-Middle">
                <img
                    src = {searchImg}
                    alt = ""
                />
            </div>
            <div className = "Search-Middle">
                <form className='searchBar'>
                    <input className='searchInput' type='text' value={search} onChange={handleSearch}/>
                </form>
            </div>
            <div className = "Search-Middle">
                <p></p>
            </div>
        </div>
    </div>
  );
}

export default Search;
